app.introStep={
	search:[
		{element:".searchMenu", intro:"Here is the search & filter area. You can search hitorical tweets or filter the realtime tweets", position:"bottom"},
		{element:$("#search-keyword").parent()[0], intro:"You can either input a keyword here or try the following combinations to find what you are interested. <p></p><ul><li>wildfire, la mesa<span>search 'wildfire' and 'la mesa' tweets</span></li><li>wildfire, -la mesa<span>search 'wildfire' but no 'la mesa' tweets</span></li><li>wildfire, la mesa, -lake<span>search 'wildfire' and 'la mesa' but no 'lake' tweets</span></li></ul>", position:"bottom"},
		{element:$(".search-placename").parent()[0], intro:"You can use the following methods to give a spatail boundary to search / filter tweets. <p></p><ul><li>Place name and Radius<span>You can just input your interested place name and radius (only miles and default is 10 miles). GeoViewer will help you locate the place and draw a rectangle based on the radius.</span></li><li>Draw a polygon on the map<span>You can click on the icon and draw a polygon on the map to define your interested boundary.</span></li><li>Draw a rectangle on the map<span>You can draw a rectangle on the map to define your interested boundary.</span></li><li>Clear the boundary<span>You can click on the icon to clear any boundries you have made.</span></li></ul>", position:"bottom"},
		{element:$("#search-startDate").parent()[0], intro:"You may input a start date to search historical tweets. If you leave it blank, GeoViewer will only filter the realtime tweets", position:"bottom"},
		{element:$("#search-endDate").parent()[0], intro:"You may input a end date to search historical tweets. If you leave it blank, GeoViewer will only filter the realtime tweets", position:"bottom"},
		{element:$(".search-viewMode").parent()[0], intro:"You can switch viewmode into image-only tweets, text-only tweets, or both", position:"bottom"}
	],
	map:[
		{element:".leaflet-control-mapGallery li[layer='markerGroup']", intro:"Show markers on the map. You can also change the symbol by mouseovering the icon", position:"left"},
		{element:".leaflet-control-mapGallery li[layer='markerCluster']", intro:"Show cluster on the map. Each cluster shows the total number of its nearest tweets and top 3 keywords in the tweets", position:"left"},
		{element:".leaflet-control-mapGallery li[layer='heatmap']", intro:"Show heatmap on the map. You may change the radius of heatmap by mouseovering the icon", position:"left"},
		{element:".leaflet-control-tocThumbnail", intro:"You can switch various base maps and overlay some featured layers on the map", position:"left"}
	],
	searchResult:[
		{element:".tbx-count", intro:"Here is showing the total number of realtime tweets or search results", position:"bottom"},
		{element:".dropdown-playpause", intro:"You can decide the refresh speed of showing tweets", position:"bottom"},
		{element:"#tbx-sort", intro:"Sort tweets by Oldest First or Newest First", position:"bottom"}
	]
}
